// ============================================================================
// RAG SOLUTION GENERATOR (No Code Analysis Needed)
// For: INFRA_ERROR, DEPENDENCY_ERROR, CONFIG_ERROR
// ============================================================================

const input = $input.first().json;

console.log('ðŸŽ¯ RAG Solution Generator: Processing', input.error_category);

// Extract best solution from similar errors
const similarSolutions = input.similar_solutions || [];

let rootCause = 'Unknown';
let fixRecommendation = 'Manual investigation required';
let preventionStrategy = 'Monitor for recurrence';
let confidence = 0.0;

if (similarSolutions.length > 0) {
  // Use highest confidence solution
  const bestSolution = similarSolutions[0];

  rootCause = bestSolution.root_cause || 'Based on historical data';
  fixRecommendation = bestSolution.solution || bestSolution.fix_recommendation;
  preventionStrategy = bestSolution.prevention || 'Follow best practices';
  confidence = bestSolution.confidence || 0.85;
}

// Generate structured solution
const solution = {
  // Original data
  build_id: input.build_id,
  error_category: input.error_category,
  error_log: input.error_log,

  // Analysis metadata
  analysis_type: 'RAG_BASED',
  analysis_timestamp: new Date().toISOString(),
  processing_time_ms: 5000, // Typical RAG processing time
  confidence_score: confidence,

  // Solution components
  root_cause: rootCause,
  fix_recommendation: fixRecommendation,
  prevention_strategy: preventionStrategy,

  // Additional context
  similar_cases_found: similarSolutions.length,
  requires_code_review: false,
  github_files_analyzed: [],

  // Recommendations
  action_required: confidence > 0.8 ? 'APPLY_FIX' : 'REVIEW_RECOMMENDED',
  priority: input.error_category === 'INFRA_ERROR' ? 'HIGH' : 'MEDIUM',

  // Cost tracking
  token_usage: 0, // No AI API call
  estimated_cost_usd: 0.01
};

console.log('âœ… RAG Solution Generated:', {
  category: solution.error_category,
  confidence: solution.confidence_score,
  action: solution.action_required
});

return { json: solution };

// ============================================================================
// CLAUDE MCP RESPONSE PARSER
// Extracts solution from Claude's response
// ============================================================================

const input = $input.first().json;

console.log('ðŸ¤– Parsing Claude MCP response...');

// Extract content from Claude response
const content = input.content || [];
let solutionText = '';

for (const block of content) {
  if (block.type === 'text') {
    solutionText += block.text;
  }
}

// Try to extract JSON from response
let parsedSolution = {};
const jsonMatch = solutionText.match(/```json\s*([\s\S]*?)\s*```/);

if (jsonMatch) {
  try {
    parsedSolution = JSON.parse(jsonMatch[1]);
  } catch (e) {
    console.warn('Failed to parse JSON from response', e);
    parsedSolution = {
      root_cause: 'Extracted from text',
      fix_recommendation: solutionText.substring(0, 500)
    };
  }
}

// Calculate token usage
const tokenUsage = input.usage || {};
const inputTokens = tokenUsage.input_tokens || 0;
const outputTokens = tokenUsage.output_tokens || 0;
const totalTokens = inputTokens + outputTokens;

// Estimate cost (Claude 3.5 Sonnet pricing)
const inputCost = (inputTokens / 1000000) * 3.00;  // $3 per million input tokens
const outputCost = (outputTokens / 1000000) * 15.00; // $15 per million output tokens
const totalCost = inputCost + outputCost;

// Build final solution object
const solution = {
  // Original data
  build_id: $('mongodb-get-minimal').first().json.build_id,
  error_category: $('langgraph-classify').first().json.error_category,
  error_log: $('mongodb-get-minimal').first().json.error_log,

  // Analysis metadata
  analysis_type: 'MCP_CLAUDE_ANALYSIS',
  analysis_timestamp: new Date().toISOString(),
  processing_time_ms: 15000, // Typical MCP processing time
  confidence_score: parsedSolution.confidence_score || 0.90,

  // Solution from Claude
  root_cause: parsedSolution.root_cause || 'See full response',
  fix_recommendation: parsedSolution.fix_recommendation || solutionText,
  test_case: parsedSolution.test_case || '',
  prevention_strategy: parsedSolution.prevention_strategy || '',

  // Code analysis details
  requires_code_review: true,
  github_files_analyzed: parsedSolution.files_analyzed || [],

  // Recommendations
  action_required: 'CODE_REVIEW_REQUIRED',
  priority: 'HIGH',

  // Cost tracking
  token_usage: totalTokens,
  token_breakdown: {
    input: inputTokens,
    output: outputTokens
  },
  estimated_cost_usd: totalCost.toFixed(4),

  // Full Claude response (for debugging)
  claude_response_raw: solutionText
};

console.log('âœ… Claude Analysis Parsed:', {
  category: solution.error_category,
  tokens: solution.token_usage,
  cost: solution.estimated_cost_usd,
  files_analyzed: solution.github_files_analyzed.length
});

return { json: solution };

