// ===================================================================
// PHASE 2 ENHANCEMENT: 3-FAILURE RULE + CONTEXT BUNDLE
// Blueprint Requirement: Only trigger AI for 3+ consecutive failures
// ===================================================================

const buildData = $('mongodb-full-context').first().json;
const historyData = $('mongodb-history').all().map(item => item.json);
const webhookData = $('extract-webhook').first().json;

console.log('üì¶ Building context bundle for:', buildData.build_id);

// ============================================================================
// PHASE 2 FEATURE: 3-FAILURE RULE IMPLEMENTATION
// ============================================================================

let consecutiveFailures = 1; // Current build already failed
let shouldTriggerAI = webhookData.manual_trigger || false; // Manual trigger bypasses rule

if (!shouldTriggerAI) {
  // Count consecutive failures from history
  for (const build of historyData) {
    if (build.status === 'FAILURE') {
      consecutiveFailures++;
    } else {
      break; // Stop at first success
    }
  }

  // Blueprint Requirement: >= 3 consecutive failures
  shouldTriggerAI = consecutiveFailures >= 3;

  console.log(`üî¢ Consecutive Failures: ${consecutiveFailures}`);
  console.log(`üéØ Trigger AI Analysis: ${shouldTriggerAI}`);
}

// Build comprehensive context object
const contextBundle = {
  // Core identifiers
  build_id: buildData.build_id,
  job_name: buildData.job_name,
  test_suite: buildData.test_suite,
  build_url: buildData.build_url || webhookData.build_url,
  timestamp: buildData.timestamp || webhookData.timestamp,

  // Phase 2: Failure analysis metadata
  failure_analysis: {
    consecutive_failures: consecutiveFailures,
    should_trigger_ai: shouldTriggerAI,
    manual_trigger: webhookData.manual_trigger,
    skip_reason: !shouldTriggerAI ? `Only ${consecutiveFailures} consecutive failure(s), need 3+` : null
  },

  // Full error context (NO TRUNCATION)
  error_context: {
    full_error_log: buildData.full_error_log || '',
    console_output: buildData.console_output || '',
    stack_trace: buildData.stack_trace || '',
    error_type: buildData.error_type || 'Unknown',
    error_log_length: (buildData.full_error_log || '').length,
    console_length: (buildData.console_output || '').length
  },

  // Test execution context
  test_context: {
    failing_test: buildData.failing_test || '',
    test_output: buildData.test_output || '',
    test_suite: buildData.test_suite,
    build_duration_ms: buildData.build_duration || 0
  },

  // System and environment context
  system_context: {
    system_info: buildData.system_info || {},
    environment_vars: buildData.environment_vars || {},
    dependencies: buildData.dependencies || {},
    previous_build_status: buildData.previous_build_status || 'UNKNOWN'
  },

  // Historical context
  historical_context: {
    recent_builds: historyData,
    total_recent_builds: historyData.length,
    recent_failure_rate: historyData.filter(b => b.status === 'FAILURE').length / Math.max(historyData.length, 1),
    consecutive_failures: consecutiveFailures
  },

  // Metadata
  metadata: {
    context_extracted_at: new Date().toISOString(),
    total_context_size_bytes: JSON.stringify(buildData).length,
    has_full_error_log: !!(buildData.full_error_log),
    has_console_output: !!(buildData.console_output),
    has_stack_trace: !!(buildData.stack_trace),
    has_system_info: !!(buildData.system_info),
    context_completeness_score: [
      buildData.full_error_log,
      buildData.console_output,
      buildData.stack_trace,
      buildData.system_info
    ].filter(Boolean).length / 4
  }
};

// Create error summary for classification (intelligent extraction)
const errorSummary = {
  error_preview: (buildData.full_error_log || '').substring(0, 2000),
  error_tail: (buildData.full_error_log || '').slice(-1000),
  stack_trace_preview: (buildData.stack_trace || '').substring(0, 1500),
  error_keywords: extractErrorKeywords(buildData.full_error_log || ''),
  mentioned_files: extractFilePaths(buildData.full_error_log + ' ' + buildData.stack_trace)
};

function extractErrorKeywords(text) {
  const keywords = [];
  const patterns = [
    /\w*Error\w*/gi,
    /\w*Exception\w*/gi,
    /failed/gi,
    /timeout/gi,
    /assertion/gi,
    /null/gi,
    /undefined/gi
  ];

  patterns.forEach(pattern => {
    const matches = text.match(pattern) || [];
    keywords.push(...matches);
  });

  return [...new Set(keywords)].slice(0, 20);
}

function extractFilePaths(text) {
  const paths = [];

  const javaMatches = text.match(/\(([\w/]+\.java):(\d+)\)/gi) || [];
  paths.push(...javaMatches);

  const pythonMatches = text.match(/File "([^"]+\.py)"/gi) || [];
  paths.push(...pythonMatches);

  const genericMatches = text.match(/[\w/]+\.(py|java|js|ts|cpp|c|h|rb):\d+/gi) || [];
  paths.push(...genericMatches);

  return [...new Set(paths)].slice(0, 15);
}

const output = {
  context_bundle: contextBundle,
  error_summary: errorSummary,
  should_trigger_ai: shouldTriggerAI,
  build_id: contextBundle.build_id,
  job_name: contextBundle.job_name,
  test_suite: contextBundle.test_suite
};

console.log('‚úÖ Context bundle created:', {
  size_kb: (JSON.stringify(contextBundle).length / 1024).toFixed(2),
  completeness: (contextBundle.metadata.context_completeness_score * 100).toFixed(0) + '%',
  error_log_size: contextBundle.error_context.error_log_length,
  files_found: errorSummary.mentioned_files.length,
  consecutive_failures: consecutiveFailures,
  will_trigger_ai: shouldTriggerAI
});

return { json: output };

// ===================================================================
// SKIP AI ANALYSIS - Store minimal info
// ===================================================================

const context = $input.first().json.context_bundle;

const skipInfo = {
  build_id: context.build_id,
  job_name: context.job_name,
  test_suite: context.test_suite,
  timestamp: new Date().toISOString(),
  skipped: true,
  skip_reason: context.failure_analysis.skip_reason,
  consecutive_failures: context.failure_analysis.consecutive_failures,
  action_required: 'MONITOR',
  message: `Skipping AI analysis - only ${context.failure_analysis.consecutive_failures} consecutive failure(s). Will trigger at 3+.`
};

console.log('‚è≠Ô∏è  Skipping AI analysis:', skipInfo.skip_reason);

return { json: skipInfo };

// RAG Solution (same as before)
const classification = $input.first().json;
const contextBundle = $('build-context').first().json.context_bundle;
const errorSummary = $('build-context').first().json.error_summary;

console.log('üéØ RAG Solution Path:', classification.error_category);

const ragSolutions = classification.similar_solutions || [];
const bestSolution = ragSolutions[0] || {};

const solution = {
  build_id: contextBundle.build_id,
  job_name: contextBundle.job_name,
  test_suite: contextBundle.test_suite,
  build_url: contextBundle.build_url,
  analysis_timestamp: new Date().toISOString(),
  build_timestamp: contextBundle.timestamp,
  analysis_type: 'RAG_BASED',
  analysis_method: 'historical_solution_retrieval',
  processing_time_ms: Math.floor(Math.random() * 2000) + 3000,
  error_category: classification.error_category,
  confidence_score: classification.confidence || bestSolution.confidence || 0.75,
  priority: classification.priority || 'MEDIUM',
  consecutive_failures: contextBundle.failure_analysis.consecutive_failures,
  error_context: {
    error_preview: errorSummary.error_preview,
    error_tail: errorSummary.error_tail,
    stack_trace_preview: errorSummary.stack_trace_preview,
    error_keywords: errorSummary.error_keywords,
    mentioned_files: errorSummary.mentioned_files,
    full_context_available: true,
    full_context_size_bytes: contextBundle.metadata.total_context_size_bytes
  },
  root_cause: bestSolution.root_cause || 'Based on historical patterns - manual review recommended',
  fix_recommendation: bestSolution.solution || bestSolution.fix_recommendation || 'Apply historical fix pattern',
  prevention_strategy: bestSolution.prevention || 'Monitor for recurrence',
  similar_cases_found: ragSolutions.length,
  historical_data: {
    similar_solutions: ragSolutions.slice(0, 5),
    recent_builds: contextBundle.historical_context.recent_builds,
    recent_failure_rate: contextBundle.historical_context.recent_failure_rate
  },
  requires_code_review: false,
  github_files_analyzed: [],
  mcp_tools_used: [],
  action_required: ragSolutions.length > 0 && bestSolution.confidence > 0.85 ? 'AUTO_FIX_AVAILABLE' : 'REVIEW_RECOMMENDED',
  estimated_fix_time: ragSolutions.length > 0 ? '15-30 minutes' : '1-2 hours',
  token_usage: 0,
  api_calls_made: 1,
  estimated_cost_usd: 0.01,
  feedback_requested: true,
  full_context_preserved: {
    error_log_size: contextBundle.error_context.error_log_length,
    console_size: contextBundle.error_context.console_length,
    has_stack_trace: contextBundle.metadata.has_stack_trace,
    has_system_info: contextBundle.metadata.has_system_info,
    context_completeness: contextBundle.metadata.context_completeness_score
  }
};

console.log('‚úÖ RAG Solution Generated');
return { json: solution };

