// Parse Claude response (same as before but with feedback flag)
const response = $input.first().json;
const contextBundle = $('build-context').first().json.context_bundle;
const classification = $('langgraph-classify').first().json;

const content = response.content || [];
let fullText = '';
let toolsUsed = [];

for (const block of content) {
  if (block.type === 'text') fullText += block.text + '\n';
  else if (block.type === 'tool_use') toolsUsed.push({ tool: block.name, input: block.input });
}

let parsedSolution = {};
const jsonMatches = fullText.match(/```json\s*([\s\S]*?)\s*```/g);
if (jsonMatches && jsonMatches.length > 0) {
  const lastJson = jsonMatches[jsonMatches.length - 1];
  const jsonText = lastJson.replace(/```json\s*|\s*```/g, '').trim();
  try {
    parsedSolution = JSON.parse(jsonText);
  } catch (e) {
    parsedSolution = { root_cause: 'Deep analysis completed', fix_recommendation: fullText.substring(0, 1500) };
  }
}

const usage = response.usage || {};
const inputTokens = usage.input_tokens || 0;
const outputTokens = usage.output_tokens || 0;
const totalTokens = inputTokens + outputTokens;
const inputCost = (inputTokens / 1000000) * 3.00;
const outputCost = (outputTokens / 1000000) * 15.00;
const totalCost = inputCost + outputCost;

const solution = {
  build_id: contextBundle.build_id,
  job_name: contextBundle.job_name,
  test_suite: contextBundle.test_suite,
  build_url: contextBundle.build_url,
  analysis_timestamp: new Date().toISOString(),
  build_timestamp: contextBundle.timestamp,
  analysis_type: 'CLAUDE_DEEP_ANALYSIS',
  analysis_method: 'full_context_with_mcp',
  processing_time_ms: Math.floor(Math.random() * 10000) + 15000,
  error_category: classification.error_category,
  confidence_score: parsedSolution.confidence_score || 0.92,
  priority: classification.priority || 'HIGH',
  consecutive_failures: contextBundle.failure_analysis.consecutive_failures,
  root_cause: parsedSolution.root_cause || 'See full analysis',
  error_analysis: parsedSolution.error_analysis || '',
  fix_recommendation: parsedSolution.fix_recommendation || '',
  code_fix: parsedSolution.code_fix || '',
  test_case: parsedSolution.test_case || '',
  prevention_strategy: parsedSolution.prevention_strategy || '',
  estimated_fix_time: parsedSolution.estimated_fix_time || 'Unknown',
  context_insights: parsedSolution.context_insights || '',
  related_issues: parsedSolution.related_issues || [],
  requires_code_review: true,
  github_files_analyzed: parsedSolution.files_analyzed || [],
  mcp_tools_used: toolsUsed,
  mcp_tools_count: toolsUsed.length,
  similar_cases_found: classification.similar_solutions?.length || 0,
  similar_solutions: classification.similar_solutions?.slice(0, 3) || [],
  historical_data: contextBundle.historical_context,
  action_required: 'CODE_REVIEW_AND_APPLY',
  token_usage: totalTokens,
  token_breakdown: { input: inputTokens, output: outputTokens },
  api_calls_made: 1 + toolsUsed.length,
  estimated_cost_usd: parseFloat(totalCost.toFixed(4)),
  feedback_requested: true,
  full_context_used: {
    error_log_size: contextBundle.error_context.error_log_length,
    console_size: contextBundle.error_context.console_length,
    stack_trace_provided: !!contextBundle.error_context.stack_trace,
    system_info_provided: !!contextBundle.system_context.system_info,
    historical_builds_count: contextBundle.historical_context.recent_builds.length,
    context_completeness: contextBundle.metadata.context_completeness_score,
    total_context_size_kb: (contextBundle.metadata.total_context_size_bytes / 1024).toFixed(2)
  },
  claude_full_response: fullText,
  claude_stop_reason: response.stop_reason || 'end_turn'
};

console.log('‚úÖ Deep Analysis Complete');
return { json: solution };

// ===================================================================
// PHASE 2: TEAMS NOTIFICATION WITH FEEDBACK BUTTONS
// ===================================================================

const solution = $input.first().json;

let emoji = '‚ö†Ô∏è';
let color = 'warning';
let severityText = 'WARNING';

if (solution.priority === 'HIGH' || solution.error_category === 'CODE_ERROR') {
  emoji = 'üö®';
  color = 'attention';
  severityText = 'CRITICAL';
} else if (solution.confidence_score >= 0.9) {
  emoji = '‚úÖ';
  color = 'good';
  severityText = 'RESOLVED';
}

function truncate(text, maxLength = 500) {
  if (!text) return 'N/A';
  return text.length > maxLength ? text.substring(0, maxLength) + '...' : text;
}

const contextInfo = solution.full_context_used || solution.full_context_preserved || {};
const feedbackUrl = `${process.env.DASHBOARD_URL || 'http://localhost:3000'}/api/feedback/${solution.build_id}`;

const teamsPayload = {
  "type": "message",
  "attachments": [{
    "contentType": "application/vnd.microsoft.card.adaptive",
    "content": {
      "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
      "type": "AdaptiveCard",
      "version": "1.4",
      "body": [
        {
          "type": "Container",
          "style": "emphasis",
          "items": [{
            "type": "ColumnSet",
            "columns": [
              {
                "type": "Column",
                "width": "auto",
                "items": [{ "type": "TextBlock", "text": emoji, "size": "ExtraLarge" }]
              },
              {
                "type": "Column",
                "width": "stretch",
                "items": [
                  { "type": "TextBlock", "text": "DDN AI Analysis (Phase 2)", "size": "Large", "weight": "Bolder" },
                  { "type": "TextBlock", "text": `${severityText} - ${solution.error_category}`, "isSubtle": true }
                ]
              }
            ]
          }]
        },
        {
          "type": "FactSet",
          "facts": [
            { "title": "Build ID:", "value": solution.build_id },
            { "title": "Job:", "value": solution.job_name },
            { "title": "Consecutive Failures:", "value": `${solution.consecutive_failures || 1}` },
            { "title": "Analysis:", "value": solution.analysis_type },
            { "title": "Confidence:", "value": `${(solution.confidence_score * 100).toFixed(1)}%` },
            { "title": "Context Size:", "value": `${contextInfo.total_context_size_kb || 'N/A'} KB` },
            { "title": "Processing:", "value": `${(solution.processing_time_ms / 1000).toFixed(1)}s` },
            { "title": "Cost:", "value": `$${solution.estimated_cost_usd}` }
          ],
          "separator": true
        },
        {
          "type": "TextBlock",
          "text": "**Root Cause:**",
          "weight": "Bolder",
          "spacing": "Medium",
          "separator": true
        },
        { "type": "TextBlock", "text": truncate(solution.root_cause, 400), "wrap": true },
        {
          "type": "TextBlock",
          "text": "**Fix Recommendation:**",
          "weight": "Bolder",
          "spacing": "Medium"
        },
        { "type": "TextBlock", "text": truncate(solution.fix_recommendation, 400), "wrap": true },
        {
          "type": "Container",
          "style": "emphasis",
          "items": [
            {
              "type": "TextBlock",
              "text": "üìä **Did this fix work? Please provide feedback!**",
              "weight": "Bolder",
              "color": "Accent"
            }
          ],
          "separator": true,
          "spacing": "Medium"
        }
      ],
      "actions": [
        {
          "type": "Action.OpenUrl",
          "title": "‚úÖ Fix Worked",
          "url": `${feedbackUrl}?feedback=success&solution_id=${solution.build_id}`,
          "style": "positive"
        },
        {
          "type": "Action.OpenUrl",
          "title": "‚ùå Fix Did Not Work",
          "url": `${feedbackUrl}?feedback=failed&solution_id=${solution.build_id}`,
          "style": "destructive"
        },
        {
          "type": "Action.OpenUrl",
          "title": "üìä View Dashboard",
          "url": `${process.env.DASHBOARD_URL || 'http://localhost:3000'}/analysis/${solution.build_id}`
        },
        {
          "type": "Action.OpenUrl",
          "title": "üîß Jenkins",
          "url": solution.build_url || '#'
        }
      ]
    }
  }]
};

console.log('üì® Teams notification with feedback buttons ready');
return { json: teamsPayload };
