const webhookData = $input.item.json;
const buildId = webhookData.build_id;
const jobName = webhookData.job_name;
const testSuite = webhookData.test_suite || 'unknown';
const buildUrl = webhookData.build_url || '';
const manualTrigger = webhookData.manual_trigger || false;

return {
  build_id: buildId,
  job_name: jobName,
  test_suite: testSuite,
  build_url: buildUrl,
  manual_trigger: manualTrigger,
  timestamp: new Date().toISOString()
};

const webhookData = $('Extract Data').item.json;
const contextData = $('Get Full Context').item.json;
const historyData = $('Get Build History').all().map(i => i.json);

let consecutiveFailures = 1;
for (const build of historyData) {
  if (build.status === 'FAILURE') {
    consecutiveFailures++;
  } else {
    break;
  }
}

const shouldTriggerAI = consecutiveFailures >= 3 || webhookData.manual_trigger;

const fullContext = {
  build_id: webhookData.build_id,
  job_name: webhookData.job_name,
  test_suite: webhookData.test_suite,
  build_url: webhookData.build_url,
  consecutive_failures: consecutiveFailures,
  manual_trigger: webhookData.manual_trigger,
  should_trigger_ai: shouldTriggerAI,
  full_logs: contextData[0]?.console_logs?.[0]?.log || '',
  test_results: JSON.stringify(contextData[0]?.test_results || []),
  error_details: JSON.stringify(contextData[0]?.error_details || []),
  system_info: JSON.stringify(contextData[0]?.system_info || {}),
  timestamp: new Date().toISOString()
};

return fullContext;

const ragResult = $json.similar_solutions?.[0] || {};
return {
  build_id: $('Build Context + 3-Failure Rule').item.json.build_id,
  error_category: $('LangGraph Classification').item.json.error_category,
  root_cause: ragResult.root_cause || 'Similar solution found',
  fix_recommendation: ragResult.fix_recommendation || 'Apply known fix',
  confidence_score: ragResult.confidence_score || 0.85,
  solution_source: 'RAG',
  mcp_tools_used: []
};

return {
  build_id: $('Build Context + 3-Failure Rule').item.json.build_id,
  error_category: $('LangGraph Classification').item.json.error_category,
  root_cause: $json.root_cause,
  fix_recommendation: $json.fix_recommendation,
  confidence_score: $json.confidence_score,
  solution_source: 'Claude MCP',
  mcp_tools_used: $json.mcp_tools_used || []
};

