// ===================================================================
// CONTEXT BUNDLE BUILDER
// Combines all data sources into rich context object
// ===================================================================

const buildData = $('mongodb-full-context').first().json;
const historyData = $('mongodb-history').all().map(item => item.json);
const webhookData = $('extract-webhook').first().json;

console.log('ðŸ“¦ Building complete context bundle for:', buildData.build_id);

// Build comprehensive context object
const contextBundle = {
  // Core identifiers
  build_id: buildData.build_id,
  job_name: buildData.job_name,
  test_suite: buildData.test_suite,
  build_url: buildData.build_url || webhookData.build_url,
  timestamp: buildData.timestamp || webhookData.timestamp,

  // Full error context (NO TRUNCATION)
  error_context: {
    full_error_log: buildData.full_error_log || '',
    console_output: buildData.console_output || '',
    stack_trace: buildData.stack_trace || '',
    error_type: buildData.error_type || 'Unknown',
    error_log_length: (buildData.full_error_log || '').length,
    console_length: (buildData.console_output || '').length
  },

  // Test execution context
  test_context: {
    failing_test: buildData.failing_test || '',
    test_output: buildData.test_output || '',
    test_suite: buildData.test_suite,
    build_duration_ms: buildData.build_duration || 0
  },

  // System and environment context
  system_context: {
    system_info: buildData.system_info || {},
    environment_vars: buildData.environment_vars || {},
    dependencies: buildData.dependencies || {},
    previous_build_status: buildData.previous_build_status || 'UNKNOWN'
  },

  // Historical context
  historical_context: {
    recent_builds: historyData,
    total_recent_builds: historyData.length,
    recent_failure_rate: historyData.filter(b => b.status === 'FAILURE').length / Math.max(historyData.length, 1)
  },

  // Metadata
  metadata: {
    context_extracted_at: new Date().toISOString(),
    total_context_size_bytes: JSON.stringify(buildData).length,
    has_full_error_log: !!(buildData.full_error_log),
    has_console_output: !!(buildData.console_output),
    has_stack_trace: !!(buildData.stack_trace),
    has_system_info: !!(buildData.system_info),
    context_completeness_score: [
      buildData.full_error_log,
      buildData.console_output,
      buildData.stack_trace,
      buildData.system_info
    ].filter(Boolean).length / 4
  }
};

// Create error summary for classification (intelligent extraction)
const errorSummary = {
  // First 2000 chars of error (for quick classification)
  error_preview: (buildData.full_error_log || '').substring(0, 2000),

  // Last 1000 chars (often contains root cause)
  error_tail: (buildData.full_error_log || '').slice(-1000),

  // Stack trace summary (first and last frames)
  stack_trace_preview: (buildData.stack_trace || '').substring(0, 1500),

  // Extract key error patterns
  error_keywords: extractErrorKeywords(buildData.full_error_log || ''),

  // File paths mentioned in error
  mentioned_files: extractFilePaths(buildData.full_error_log + ' ' + buildData.stack_trace)
};

function extractErrorKeywords(text) {
  const keywords = [];
  const patterns = [
    /\w*Error\w*/gi,
    /\w*Exception\w*/gi,
    /failed/gi,
    /timeout/gi,
    /assertion/gi,
    /null/gi,
    /undefined/gi
  ];

  patterns.forEach(pattern => {
    const matches = text.match(pattern) || [];
    keywords.push(...matches);
  });

  // Return unique keywords, top 20
  return [...new Set(keywords)].slice(0, 20);
}

function extractFilePaths(text) {
  const paths = [];

  // Java files: at com.example.Class.method(File.java:123)
  const javaMatches = text.match(/\(([\w/]+\.java):(\d+)\)/gi) || [];
  paths.push(...javaMatches);

  // Python files: File "/path/to/file.py", line 123
  const pythonMatches = text.match(/File "([^"]+\.py)"/gi) || [];
  paths.push(...pythonMatches);

  // Generic paths
  const genericMatches = text.match(/[\w/]+\.(py|java|js|ts|cpp|c|h|rb):\d+/gi) || [];
  paths.push(...genericMatches);

  return [...new Set(paths)].slice(0, 15);
}

const output = {
  context_bundle: contextBundle,
  error_summary: errorSummary,

  // For downstream nodes
  build_id: contextBundle.build_id,
  job_name: contextBundle.job_name,
  test_suite: contextBundle.test_suite
};

console.log('âœ… Context bundle created:', {
  size_kb: (JSON.stringify(contextBundle).length / 1024).toFixed(2),
  completeness: (contextBundle.metadata.context_completeness_score * 100).toFixed(0) + '%',
  error_log_size: contextBundle.error_context.error_log_length,
  files_found: errorSummary.mentioned_files.length
});

return { json: output };

