const contextBundle = $('build-context').first().json.context_bundle;
const errorSummary = $('build-context').first().json.error_summary;

console.log('ðŸŽ¯ RAG Solution Path:', classification.error_category);

// Get best RAG solutions
const ragSolutions = classification.similar_solutions || [];
const bestSolution = ragSolutions[0] || {};

// Build solution using RAG + full context
const solution = {
