  estimated_fix_time: ragSolutions.length > 0 ? '15-30 minutes' : '1-2 hours',

  // Cost tracking
  token_usage: 0,
  api_calls_made: 1,
  estimated_cost_usd: 0.01,

  // Full context reference
  full_context_preserved: {
    error_log_size: contextBundle.error_context.error_log_length,
    console_size: contextBundle.error_context.console_length,
    has_stack_trace: contextBundle.metadata.has_stack_trace,
    has_system_info: contextBundle.metadata.has_system_info,
    context_completeness: contextBundle.metadata.context_completeness_score
  }
};

console.log('âœ… RAG Solution Generated:', {
  category: solution.error_category,
  confidence: solution.confidence_score,
  similar_found: solution.similar_cases_found,
  action: solution.action_required,
  context_size_kb: (contextBundle.metadata.total_context_size_bytes / 1024).toFixed(2)
