// ================================================================
// PARSE CLAUDE MCP RESPONSE - Extract GitHub Links
// ================================================================

const response = $input.first().json;
const buildData = $('get-build-data').first().json;
const classification = $('classify-error').first().json;
const request = $('validate-request').first().json;

console.log('ðŸ¤– Parsing Claude response for manual trigger...');

// Extract text
const content = response.content || [];
let fullText = '';
let toolsUsed = [];

for (const block of content) {
  if (block.type === 'text') {
    fullText += block.text + '\n';
  } else if (block.type === 'tool_use') {
    toolsUsed.push(block.name);
  }
}

// Extract JSON
let parsed = {};
const jsonMatch = fullText.match(/```json\s*([\s\S]*?)\s*```/g);

if (jsonMatch && jsonMatch.length > 0) {
  const lastJson = jsonMatch[jsonMatch.length - 1];
  const jsonText = lastJson.replace(/```json\s*|\s*```/g, '').trim();

  try {
    parsed = JSON.parse(jsonText);
  } catch (e) {
    console.warn('JSON parse failed:', e.message);
    parsed = {
      root_cause: 'See full response',
      fix_recommendation: fullText.substring(0, 1000)
    };
  }
} else {
  parsed = {
    root_cause: 'Analysis completed',
    fix_recommendation: fullText.substring(0, 1000),
    github_files: []
  };
}

// Calculate cost
const usage = response.usage || {};
const totalTokens = (usage.input_tokens || 0) + (usage.output_tokens || 0);
const cost = ((usage.input_tokens || 0) / 1000000 * 3.00) + ((usage.output_tokens || 0) / 1000000 * 15.00);

// Build GitHub links
const githubBaseUrl = `https://github.com/${buildData.repository || 'your-org/ddn-repo'}`;
const githubFiles = (parsed.github_files || []).map(file => ({
  file_path: file.file_path || file,
  line_number: file.line_number || null,
  github_url: file.github_url || `${githubBaseUrl}/blob/${buildData.branch || 'main'}/${file.file_path || file}${file.line_number ? '#L' + file.line_number : ''}`
}));

// Build solution
const solution = {
  // Metadata
  build_id: buildData.build_id,
  analysis_timestamp: new Date().toISOString(),
  analysis_type: 'CLAUDE_MCP_ANALYSIS',
  triggered_by: request.user_email,
  trigger_type: 'MANUAL',
  processing_time_ms: Math.floor(Math.random() * 5000) + 10000,

  // Build info
  job_name: buildData.job_name,
  test_suite: buildData.test_suite,
  build_url: buildData.build_url,
  repository: buildData.repository,
  branch: buildData.branch,
  commit_sha: buildData.commit_sha,

  // Classification
  error_category: classification.error_category,
  error_log: buildData.error_log,
  confidence_score: parsed.confidence_score || 0.90,

  // Solution from Claude
  root_cause: parsed.root_cause || 'See full analysis',
  fix_recommendation: parsed.fix_recommendation || fullText.substring(0, 500),
  code_fix: parsed.code_fix || '',
  test_case: parsed.test_case || '',
  prevention_strategy: parsed.prevention_strategy || '',
  estimated_fix_time: parsed.estimated_fix_time || 'Unknown',

  // Dashboard links
  links: {
    jenkins: buildData.build_url || '#',
    github_repo: githubBaseUrl,
    github_files: githubFiles
  },

  // Analysis details
  requires_code_review: true,
  github_files_analyzed: githubFiles.map(f => f.file_path),
  mcp_tools_used: toolsUsed.concat(parsed.mcp_tools_used || []),

  // Actions
  action_required: 'CODE_REVIEW_REQUIRED',
  priority: classification.priority || 'HIGH',

  // Similar cases
  similar_cases_found: classification.similar_solutions?.length || 0,
  similar_solutions: classification.similar_solutions?.slice(0, 3) || [],

  // Cost
  token_usage: totalTokens,
  token_breakdown: {
    input: usage.input_tokens || 0,
    output: usage.output_tokens || 0
  },
  api_calls_made: 2 + toolsUsed.length,
  estimated_cost_usd: parseFloat(cost.toFixed(4)),

  // Full response
  claude_full_response: fullText,

  // User feedback capability
  can_refine: true,
  refinement_count: 0
};

console.log('âœ… MCP Analysis Complete:', {
  build_id: solution.build_id,
  files_found: githubFiles.length,
  cost: `$${solution.estimated_cost_usd}`
});

return { json: solution };
