// ================================================================
// VALIDATE REFINEMENT REQUEST
// Requires: build_id + user_feedback
// ================================================================

const request = $input.first().json;

console.log('ðŸ”„ Refinement request received:', request);

// Required fields
const buildId = request.body?.build_id || request.build_id;
const userFeedback = request.body?.user_feedback || request.user_feedback;
const userEmail = request.body?.user_email || request.user_email || 'unknown@user.com';

if (!buildId) {
  throw new Error('Missing required field: build_id');
}

if (!userFeedback || userFeedback.trim().length < 10) {
  throw new Error('User feedback is required and must be at least 10 characters');
}

// Extract additional context
const additionalContext = request.body?.additional_context || request.additional_context || {};

const validated = {
  build_id: buildId.toString(),
  user_feedback: userFeedback.trim(),
  user_email: userEmail,
  additional_context: {
    check_recent_commits: additionalContext.check_recent_commits || false,
    check_related_tests: additionalContext.check_related_tests || false,
    include_config_files: additionalContext.include_config_files || false,
    focus_areas: additionalContext.focus_areas || []
  },
  refinement_timestamp: new Date().toISOString(),
  trigger_type: 'REFINEMENT'
};

console.log('âœ… Refinement request validated');

return { json: validated };

