// ================================================================
// PARSE REFINED CLAUDE RESPONSE
// ================================================================

const response = $input.first().json;
const context = $('merge-context').first().json;
const request = $('validate-refine').first().json;

console.log('ðŸ”„ Parsing refined analysis...');

// Extract content
const content = response.content || [];
let fullText = '';
let toolsUsed = [];

for (const block of content) {
  if (block.type === 'text') {
    fullText += block.text + '\n';
  } else if (block.type === 'tool_use') {
    toolsUsed.push(block.name);
  }
}

// Extract JSON
let parsed = {};
const jsonMatch = fullText.match(/```json\s*([\s\S]*?)\s*```/g);

if (jsonMatch && jsonMatch.length > 0) {
  const lastJson = jsonMatch[jsonMatch.length - 1];
  const jsonText = lastJson.replace(/```json\s*|\s*```/g, '').trim();

  try {
    parsed = JSON.parse(jsonText);
  } catch (e) {
    console.warn('JSON parse error:', e.message);
    parsed = {
      refinement_summary: 'See full response',
      root_cause: fullText.substring(0, 500)
    };
  }
}

// Calculate cost
const usage = response.usage || {};
const totalTokens = (usage.input_tokens || 0) + (usage.output_tokens || 0);
const cost = ((usage.input_tokens || 0) / 1000000 * 3.00) + ((usage.output_tokens || 0) / 1000000 * 15.00);

// Build GitHub links
const githubBase = `https://github.com/${context.build_data.repository || 'your-org/ddn-repo'}`;
const githubFiles = (parsed.github_files || []).map(file => ({
  file_path: file.file_path || file,
  line_number: file.line_number || null,
  reason: file.reason || '',
  github_url: file.github_url || `${githubBase}/blob/${context.build_data.branch || 'main'}/${file.file_path}${file.line_number ? '#L' + file.line_number : ''}`
}));

// Build refined solution
const refinedSolution = {
  // Metadata
  build_id: context.build_data.build_id,
  analysis_timestamp: new Date().toISOString(),
  analysis_type: 'REFINED_ANALYSIS',
  refinement_version: context.refinement_count,
  triggered_by: request.user_email,

  // Build info
  job_name: context.build_data.job_name,
  test_suite: context.build_data.test_suite,
  repository: context.build_data.repository,
  branch: context.build_data.branch,
  commit_sha: context.build_data.commit_sha,
  build_url: context.build_data.build_url,

  // Refinement details
  refinement_summary: parsed.refinement_summary || 'Analysis refined based on user feedback',
  user_feedback_addressed: parsed.user_feedback_addressed || 'Feedback incorporated',
  user_feedback_text: request.user_feedback,

  // Updated classification
  error_category: parsed.revised_category || context.original_analysis.error_category,
  category_changed: (parsed.revised_category !== context.original_analysis.error_category),

  // Refined solution
  root_cause: parsed.root_cause || 'See full response',
  fix_recommendation: parsed.fix_recommendation || fullText.substring(0, 500),
  code_fix: parsed.code_fix || '',
  test_case: parsed.test_case || '',
  prevention_strategy: parsed.prevention_strategy || '',
  estimated_fix_time: parsed.estimated_fix_time || 'Unknown',

  // Evidence and explanation
  evidence: parsed.evidence || [],
  why_original_was_wrong: parsed.why_original_was_wrong || '',

  // Confidence tracking
  confidence_score: parsed.confidence_score || 0.92,
  confidence_improvement: parsed.confidence_improvement || '+0%',
  original_confidence: context.original_analysis.confidence,

  // Links
  links: {
    jenkins: context.build_data.build_url,
    github_repo: githubBase,
    github_files: githubFiles
  },

  // Analysis details
  github_files_analyzed: githubFiles.map(f => f.file_path),
  mcp_tools_used: toolsUsed.concat(parsed.mcp_tools_used || []),

  // Cost
  token_usage: totalTokens,
  token_breakdown: {
    input: usage.input_tokens || 0,
    output: usage.output_tokens || 0
  },
  api_calls_made: 2 + toolsUsed.length,
  estimated_cost_usd: parseFloat(cost.toFixed(4)),

  // History tracking
  refinement_count: context.refinement_count,
  refinement_history: context.previous_refinements.concat([{
    version: context.refinement_count - 1,
    timestamp: context.original_analysis.timestamp,
    category: context.original_analysis.error_category,
    root_cause: context.original_analysis.root_cause,
    confidence: context.original_analysis.confidence
  }]),

  // Original analysis reference
  original_analysis: context.original_analysis,

  // Full response
  claude_full_response: fullText,

  // Actions
  can_refine: true,
  action_required: 'REVIEW_REFINED_ANALYSIS',
  priority: 'HIGH'
};

console.log('âœ… Refinement complete:', {
  build_id: refinedSolution.build_id,
  version: refinedSolution.refinement_version,
  category_changed: refinedSolution.category_changed,
  cost: `$${refinedSolution.estimated_cost_usd}`
});

return { json: refinedSolution };
